import React, { useState } from 'react';
import { auth } from '../services/firebase'; // Asegúrate de que esta ruta sea correcta
import { signInWithEmailAndPassword } from "firebase/auth";
import '../App.css'; // Importa el archivo CSS

const Login = ({ onSwitch }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(''); // Limpia errores previos
    try {
      await signInWithEmailAndPassword(auth, email, password);
      alert("Inicio de sesión exitoso");
      window.location.href = "/home"; // Redirigir a la página principal
    } catch (error) {
      console.error("Error al iniciar sesión: ", error);
      setError("Error al iniciar sesión: " + error.message); // Captura el error y muestra el mensaje
    }
  };

  return (
    <div className="App">
      <div className="container">
        <div className="title">Bienvenido a WorkMind Zen</div>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Contraseña"
            required
          />
          <button type="submit">Iniciar sesión</button>
        </form>
        <p>
          ¿No tienes cuenta? <span onClick={onSwitch} style={{ cursor: 'pointer', color: 'blue' }}>Regístrate</span>
        </p>
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default Login;
