import React, { useState } from 'react';
import { auth } from '../services/firebase'; // Asegúrate de importar auth
import { signInWithEmailAndPassword } from "firebase/auth";

const Login = ({ onSwitch }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      await signInWithEmailAndPassword(auth, email, password); // Inicia sesión
      alert("Inicio de sesión exitoso");
      // Aquí puedes redirigir al usuario a otra página si es necesario
    } catch (error) {
      console.error("Error al iniciar sesión: ", error);
      setError("Error al iniciar sesión: " + error.message);
    }
  };

  return (
    <div>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Contraseña"
          required
        />
        <button type="submit">Iniciar sesión</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
      <p>
        ¿No tienes cuenta? <span onClick={onSwitch} style={{ cursor: 'pointer', color: 'blue' }}>Regístrate</span>
      </p>
    </div>
  );
};

export default Login;
