import React, { useState } from 'react';
import axios from 'axios';

const Register = ({ onSwitch }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // Estado para manejar errores

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(''); // Resetear el error al iniciar el registro
    try {
      const response = await axios.post('http://127.0.0.1:5000/add_user', {
        email,
        password,
      });
      if (response.data.success) {
        alert("Usuario registrado correctamente");
        // Limpiar los campos después del registro
        setEmail('');
        setPassword('');
      } else {
        setError("Error al registrar usuario"); // Mostrar mensaje de error
      }
    } catch (error) {
      console.error("Error al registrar usuario: ", error);
      setError("Error al registrar usuario"); // Mostrar mensaje de error
    }
  };

  return (
    <div>
      <form onSubmit={handleRegister}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Contraseña"
          required
        />
        <button type="submit">Registrarse</button>
        {error && <p style={{ color: 'red' }}>{error}</p>} {/* Mostrar mensaje de error */}
      </form>
      <p>
        ¿Ya tienes cuenta? <span onClick={onSwitch} style={{ cursor: 'pointer', color: 'blue' }}>Inicia sesión</span>
      </p>
    </div>
  );
};

export default Register;
