import React, { useState } from 'react';
import './App.css';
import Register from './components/Register';
import Login from './components/Login';

function App() {
  const [isRegister, setIsRegister] = useState(true); // Estado para gestionar qué componente mostrar

  const handleSwitch = () => {
    setIsRegister(!isRegister); // Cambiar entre registro y login
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>{isRegister ? "Registro de Usuario" : "Inicio de Sesión"}</h1>
        {isRegister ? <Register onSwitch={handleSwitch} /> : <Login onSwitch={handleSwitch} />}
      </header>
    </div>
  );
}

export default App;
