// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Home from './components/Home';

const App = () => {
  const [isLogin, setIsLogin] = useState(true); // Estado para controlar la vista

  const toggleForm = () => {
    setIsLogin(!isLogin); // Cambiar entre Login y Register
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login onSwitch={toggleForm} />} /> {/* Pasar la función */}
        <Route path="/register" element={<Register onSwitch={toggleForm} />} /> {/* Pasar la función */}
        <Route path="/home" element={<Home />} />
      </Routes>
    </Router>
  );
};

export default App;
