import React, { useState } from 'react';
import { auth } from '../services/firebase'; // Asegúrate de importar auth
import { createUserWithEmailAndPassword } from "firebase/auth";

const Register = ({ onSwitch }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');

    try {
      await createUserWithEmailAndPassword(auth, email, password); // Crea el usuario
      alert("Usuario registrado correctamente");
      setEmail('');
      setPassword('');
    } catch (error) {
      console.error("Error al registrar usuario: ", error);
      setError("Error al registrar usuario: " + error.message);
    }
  };

  return (
    <div>
      <form onSubmit={handleRegister}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Contraseña"
          required
        />
        <button type="submit">Registrarse</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
      <p>
        ¿Ya tienes cuenta? <span onClick={onSwitch} style={{ cursor: 'pointer', color: 'blue' }}>Inicia sesión</span>
      </p>
    </div>
  );
};

export default Register;
